/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options
  --color-accent: #004700;
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 0;
  --border-color: #e0e1e1;
  */
  --color-accent: #FEC93C;
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 0;
  --border-color: #294c80;

  	--darkColor: #294c80;
	--lightColor: #2d8fc5;
	--accentColor: #FEC93C;
	--accentDarkerColor: #FEC93C;
	--font1: "ProximaNova", sans-serif;
	--font2: "NotoSerif", serif;

	--color: #0f0f16;
	--colorGray: #b6b6d2;
	--zoom: 0.9;
}

html {
	zoom: var(--zoom);
}

html,
body {
	scroll-behavior: smooth;
	scroll-padding: 6em;
	padding: 0; 
	margin: 0;
	background-color: var(--darkColor);
	font-size: 16px;
}

.noscroll {
	overflow: hidden;
}



/* Root element (use instead of `body`) */
#page {
  	color: var(--color);


  background: white;

	font-size: 16px;
	font-family: var(--font1);
	font-weight: 300;

	a {
		text-decoration: none;
	}

	@media (hover: hover) and (pointer: fine) {
		a:hover {
			text-decoration: none;
		}
	}
}

/* Elements */
.section-container {
  max-width: 1200px;
  margin: 0 auto;
  		padding: 3rem 1rem;
		margin: 0 auto;
		width: auto;

		@media screen and (min-width: 768px) {
			width: calc(100% - 2rem);
		}
}

a.link {
  line-height: 1.3;

  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 2.5rem;
  line-height: 1.15;

}

.button {
  color: white;
  background: var(--color-accent, rebeccapurple);
  border-radius: 0;
  padding: 18px 24px;
  transition: var(--transition, 0.1s box-shadow);
  border: 0; /* reset */

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent, rebeccapurple);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent, rebeccapurple);
  }
}

/* Content Section */
.content {
  max-width: 900px;
  margin: 0 auto;
  padding: 3rem 2rem;

  p {
    margin-bottom: 1rem;
    line-height: 1.5;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
    line-height: 1.3;
    font-weight: 500;
    border-bottom: 2px solid var(--color-accent);
    transform: translateY(-2px); /* move link back into place */
    transition: var(--transition, 0.1s border);

    &:hover {
      border-color: transparent;
    }
  }

  h1 {
    font-size: 3rem;
    font-weight: 500;
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  h2 {
    font-size: 2.5rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h3 {
    font-size: 2rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    margin-top: 1.5rem;
    margin-bottom: 1.5rem;
    border-left: 5px solid var(--color-accent);
  }
}

.block2Columns {
	margin: 4rem 0;

	@media screen and (min-width: 768px) {
		display: grid;
		row-gap: 1rem;
		grid:
			'title secondColumn'
			'text secondColumn'
			/ 66.6666% 33.3333%;
		grid-template-rows: min-content 1fr;
	}

	.title {
		grid-area: title;
		position: relative;
		padding: 0 0 0.7rem 0.5rem;

		h3 {
			font-size: 1.7rem;
			padding: 0;
			margin: 0;
			font-weight: 600;
		}

		h4 {
			color: #9d9e9e;
			font-weight: normal;
			font-size: 1.4rem;
			padding: 0;
			margin: 0;
			letter-spacing: -0.05rem;
		}

		&:after {
			content: '';
			display: block;
			height: 2.5px;
			background-color: var(--accentColor);
			width: 37%;
			left: 0;
			bottom: 0;
			position: absolute;
		}
	}

	.text {
		grid-area: text;
		padding: 1rem 2rem 0.5rem 1rem;

		@media screen and (min-width: 768px) {
			padding: 0 2rem 0 1rem;
		}

		:global(strong) {
			font-weight: 600;
		}

		:global(h3) {
			font-size: 1.3rem;
			font-weight: 600;
		}

		:global(p) {
			font-size: 1.1rem;
			line-height: 1.35;
			margin: 1rem 0 .25rem 0;
			padding: 0;
		}

		:global(ul), :global(ol) {
			font-size: 1.1rem;
			line-height: 1.5;
			margin-top: 0;

		}
	}

	.secondColumn {
		grid-area: secondColumn;
	}
  .news-wrapper {
    .wrapper {
		--fontSize: 1rem;
		--imageWidth: 100px;
		overflow: hidden;
		transition: max-height 0.3s ease-in-out;
		position: relative;
		&.fullPage {
			--fontSize: 1.2rem;
			--imageWidth: 200px;
		}
		.fader {
			z-index: 2;
			height: 5rem;
			background: linear-gradient(to bottom, transparent, white, white);
			left: 0;
			right: 0;
			bottom: 0;
			position: absolute;
		}
		a.more {
			background: none;
			border: 0;
			color: var(--accentColor);
			font-size: var(--fontSize);
			font-weight: 600;
			padding: 1rem 0;
			display: block;
			position: absolute;
			bottom: 0;
			left: 0;
			right: 0;
			text-align: center;
			cursor: pointer;
			z-index: 10;
			@media (hover: hover) and (pointer: fine) {
				&:hover {
					text-decoration: underline;
				}
			}
		}
		.news-header {
			position: relative;
			padding: 0.5rem 0;
			text-transform: uppercase;
			font-size: calc(var(--fontSize) * 1.2);
			letter-spacing: 4px;
			&::after {
				content: '';
				display: block;
				height: 2.5px;
				background-color: var(--accentColor);
				width: 37%;
				left: 0;
				bottom: 0;
				position: absolute;
			}
		}

		.news {
			margin: 1rem 0;
			display: grid;
			grid:
				'. date'
				'image title'
				'image text'
				/ var(--imageWidth) 1fr;
			grid-template-rows: min-content min-content 1fr;
			column-gap: 0.5rem;

			width: 100%;
		}
		.news-date {
			grid-area: date;
			color: var(--colorGray);
		}
		.news-title {
			grid-area: title;
			font-size: var(--fontSize);
			font-weight: 600;
			margin-bottom: 10px;
		}

		.news-text {
			grid-area: text;
			font-size: var(--fontSize);
			font-weight: 400;
			margin-bottom: 10px;
			:global(a) {
				color: var(--accentDarkerColor);
				font-weight: 600;
				@media (hover: hover) and (pointer: fine) {
					&:hover {
						text-decoration: underline;
					}
				}
			}
		}
		.news-image {
			grid-area: image;
			:global(img) {
				object-position: top center;
				display: block;
				height: 100%;
				width: 100%;
				object-fit: contain;
			}
		}
	}
	.title {
		position: relative;
		padding: 0 0 0.7rem 0.5rem;

		h3 {
			font-size: 1.7rem;
			padding: 0;
			margin: 0;
			font-weight: 600;
		}

		&:after {
			content: '';
			display: block;
			height: 2.5px;
			background-color: var(--accentColor);
			width: 37%;
			left: 0;
			bottom: 0;
			position: absolute;
		}
	}
}
}

.infoLine {
	border: 2px solid var(--lightColor);
	border-radius: 3rem;
	display: flex;
	justify-content: space-between;
	flex-direction: column;
	align-items: center;
	padding: 1rem;
  margin:3rem 0 0 0;

	@media screen and (min-width: 870px) {
		flex-direction: row;
		padding: 1rem 1.2rem 1rem 3rem;
	}

	&.toRight {
		padding: 1rem;

		@media screen and (min-width: 870px) {
			justify-content: flex-end;

		}
	}


	.info {
		display: flex;
		width: 100%;
		flex-direction: column;
		font-size: 1.4rem;
		align-items: center;
		margin-bottom: 3rem;

		@media screen and (min-width: 870px) {
			width: 35%;
			flex-direction: row;
			justify-content: space-around;
			font-size: 1.2rem;
			margin-bottom: 0;
		}

		&.spacy {
			font-size: 1.2rem;

			@media screen and (min-width: 870px) {
				justify-content: flex-start;
				font-size: 1.4rem;
			}
		}

		a,
		span {
			padding: 0.5rem 0;

			@media screen and (min-width: 870px) {
				padding: 0 0.5rem;
			}

			color: var(--color1);
			transition: all 0.2s ease-in-out;

		}

		@media (hover: hover) and (pointer: fine) {
			a:hover {
				color: var(--accentDarkerColor);
			}
		}
	}

	.buttonWrapper {
		display: flex;
		width: 100%;
		flex-direction: column;
		align-items: center;

		@media screen and (min-width: 870px) {
			width: 500px;
			flex-direction: row;
			justify-content: space-evenly;
		}

		&.spacy {
			.text {
				min-width: 200px;
				text-align: left;
				font-size: 1rem;
			}

			@media screen and (min-width: 870px) {
				width: 570px;
				font-size: 1.1rem;

				.text {
					min-width: 300px;
					font-size: 1.2rem;
					text-align: right;
				}
			}

			&>a {
				@media screen and (min-width: 870px) {
					margin-left: 0.5rem;
					padding: 1rem 3rem;
					white-space: nowrap;
				}
			}
		}

		.text {
			font-weight: 600;
			font-size: 1.2rem;
			padding-bottom: 0.5rem;

			@media screen and (min-width: 870px) {
				padding-bottom: 0;
			}

			a {
				color: var(--darkColor);
				font-weight: normal;
			}
		}

		.phonesWrapper {
			display: flex;
			flex-direction: column;
			min-height: 4rem;
			justify-content: center;

			.price {
				font-size: 1.7rem;
				font-weight: 600
			}

			a {
				white-space: nowrap;
			}
		}

		&>a,
		& .phonesWrapper>a {
			padding: 1rem 2rem;
			color: white;
			background-color: var(--darkColor);
			border-radius: 30px;
			transition: all 0.2s ease-in-out;

			@media screen and (min-width: 870px) {
				margin-left: 2rem;
				padding: 1rem 3rem;

			}

			span {
				transform: scale(1);
				transition: all 0.2s ease-in-out;
				transform-origin: center;
				display: inline-block;
			}

			@media (hover: hover) and (pointer: fine) {
				&:hover {
					background-color: var(--lightColor);

					span {
						transform: scale(1.1);
					}
				}
			}
		}
	}
}

.blockWithImageAndButton {
	row-gap: 1rem;
	margin: 0 0 4rem 0;

	@media screen and (min-width: 768px) {
		display: grid;
		grid:
			'title image'
